INSERT INTO questions (exam_id, question_text, option_a, option_b, option_c, option_d, correct_answer, points, category, difficulty_level, tags, explanation) VALUES
(NULL, 'The Rajarani Temple in Bhubaneswar is famous for its unique architectural style. What is it primarily known for?', 'Its circular design', 'Its absence of a deity', 'Its golden dome', 'Its underground chambers', 'B', 1, 'Temples & Architecture', 'medium', '["Rajarani Temple", "Bhubaneswar", "Architecture", "Temples"]'::jsonb, 'The Rajarani Temple is unique because it does not house any deity. It is purely an architectural marvel, known for its beautiful carvings and sculptures.'),
(NULL, 'Which temple in Odisha is known for its "Deul" style of architecture and is considered one of the oldest temples in Bhubaneswar?', 'Mukteswar Temple', 'Parasurameswar Temple', 'Lingaraj Temple', 'Rajarani Temple', 'B', 1, 'Temples & Architecture', 'hard', '["Parasurameswar", "Deul Architecture", "Bhubaneswar", "Temples"]'::jsonb, 'Parasurameswar Temple, built in the 7th century, is one of the oldest temples in Bhubaneswar and showcases the early Deul style of Odishan temple architecture.'),
(NULL, 'The Jagannath Temple in Puri is one of the Char Dham pilgrimage sites. How many times is the temple rebuilt with new wood?', 'Every 8 years', 'Every 12 years', 'Every 19 years', 'Every 25 years', 'C', 1, 'Temples & Architecture', 'hard', '["Jagannath Temple", "Nabakalebara", "Puri", "Temples"]'::jsonb, 'The Nabakalebara ritual occurs every 19 years when the wooden idols of Jagannath, Balabhadra, Subhadra, and Sudarshan are replaced with new ones carved from a special neem tree.'),
(NULL, 'Which architectural feature is characteristic of Odishan temple architecture and represents the cosmic mountain?', 'Shikhara', 'Mandapa', 'Garbhagriha', 'Jagamohana', 'A', 1, 'Temples & Architecture', 'medium', '["Temple Architecture", "Shikhara", "Odishan Architecture"]'::jsonb, 'The Shikhara (tower) in Odishan temple architecture represents Mount Meru, the cosmic mountain, and is the most prominent feature of these temples.'),
(NULL, 'The Mukteswar Temple is often called the "Gem of Odishan Architecture". What is its most distinctive feature?', 'Its golden spire', 'Its torana (arched gateway)', 'Its underground sanctum', 'Its circular design', 'B', 1, 'Temples & Architecture', 'medium', '["Mukteswar Temple", "Torana", "Architecture", "Bhubaneswar"]'::jsonb, 'The Mukteswar Temple is famous for its elaborately carved torana (arched gateway), which is considered one of the finest examples of Odishan temple architecture.'),
(NULL, 'Which temple in Odisha is dedicated to Lord Shiva and is known for its 64 Yogini shrines?', 'Hirapur Chausath Yogini Temple', 'Lingaraj Temple', 'Mukteswar Temple', 'Parasurameswar Temple', 'A', 1, 'Temples & Architecture', 'hard', '["Hirapur", "Yogini Temple", "Shiva", "Temples"]'::jsonb, 'The Hirapur Chausath Yogini Temple near Bhubaneswar is a circular temple dedicated to 64 Yoginis, representing the feminine aspects of divinity.'),
(NULL, 'The Konark Sun Temple is designed as a chariot. How many horses are depicted pulling this chariot?', '5 horses', '6 horses', '7 horses', '8 horses', 'C', 1, 'Temples & Architecture', 'hard', '["Konark", "Sun Temple", "Chariot", "Architecture"]'::jsonb, 'The Konark Sun Temple is designed as a massive chariot with 7 horses, representing the 7 days of the week, pulling the sun god''s chariot.'),
(NULL, 'Which temple in Odisha is known for its erotic sculptures and is part of the "Temple City" of Bhubaneswar?', 'Lingaraj Temple', 'Mukteswar Temple', 'Rajarani Temple', 'Parasurameswar Temple', 'C', 1, 'Temples & Architecture', 'medium', '["Rajarani Temple", "Sculptures", "Bhubaneswar", "Temples"]'::jsonb, 'The Rajarani Temple is famous for its intricate sculptures, including some erotic carvings, and is considered one of the architectural gems of Bhubaneswar.'),

